import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [donations, setDonations] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDonations();
  }, []);

  const fetchDonations = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/donations');
      setDonations(response.data);
    } catch (error) {
      console.error('Error fetching donations:', error);
    } finally {
      setLoading(false);
    }
  };

  const verifyDonation = async (id) => {
    try {
      await axios.patch(`http://localhost:5000/api/donations/${id}/verify`);
      fetchDonations(); // Refresh the list
    } catch (error) {
      console.error('Error verifying donation:', error);
    }
  };

  if (loading) {
    return <div className="text-center py-12">Loading...</div>;
  }

  return (
    <div className="max-w-7xl mx-auto py-12 px-4 sm:px-6 lg:px-8">
      <h2 className="text-3xl font-bold text-gray-900 mb-8">Donation Dashboard</h2>
      <div className="bg-white shadow overflow-hidden sm:rounded-md">
        <ul className="divide-y divide-gray-200">
          {donations.map((donation) => (
            <li key={donation._id} className="px-6 py-4">
              <div className="flex items-center justify-between">
                <div className="flex-1">
                  <div className="flex items-center">
                    <p className="text-sm font-medium text-gray-900">{donation.name}</p>
                    <p className="ml-2 text-sm text-gray-500">{donation.email}</p>
                  </div>
                  <div className="mt-1">
                    <p className="text-sm text-gray-500">Amount: ${donation.amount}</p>
                    <p className="text-sm text-gray-500">Date: {new Date(donation.createdAt).toLocaleDateString()}</p>
                  </div>
                </div>
                <div className="flex items-center">
                  <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                    donation.verified ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                  }`}>
                    {donation.verified ? 'Verified' : 'Pending'}
                  </span>
                  {!donation.verified && (
                    <button
                      onClick={() => verifyDonation(donation._id)}
                      className="ml-4 bg-indigo-600 text-white px-3 py-1 rounded text-sm hover:bg-indigo-700"
                    >
                      Verify
                    </button>
                  )}
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
